create table if not exists posts(post_id text primary key,platform text not null default 'x',author_id text,posted_at timestamptz,like_count int,reply_count int,repost_count int,quote_count int,url text,created_at timestamptz default now());create table if not exists post_insights(post_id text primary key references posts(post_id) on delete cascade,intent text check(intent in('complaint','request','praise','other')),pain_points text[],desired_features text[],materials text[],motifs text[],summary text,created_at timestamptz default now());create table if not exists rollups_hourly(bucket timestamptz not null,intent text not null,key text not null,count int not null,primary key(bucket,intent,key));alter table posts enable row level security;alter table post_insights enable row level security;alter table rollups_hourly enable row level security;create policy if not exists "read posts" on posts for select using(true);create policy if not exists "read insights" on post_insights for select using(true);create policy if not exists "read rollups" on rollups_hourly for select using(true);